const useInputValue = initialValue => {
  const [value, setValue] = useState(initialValue);

  return {
    value,
    onChange: e => setValue(e.target.value),
    resetValue: () => setValue("")
  };
};

export default ({ onSubmit }) => {
  const { resetValue, ...text } = useInputValue("");
  return (
    <form>
      <input {...text} />
      <button type='submit'onSubmit={e => {
        e.preventDefault();
        onSubmit(text.value);
        resetValue();
      }}>Add</button>
   
    </form>
  );
};
