import React, {useState} from "react";
import "./App.css"
import Form from "./Form";

export default () => {
     const [todos, setTodos] = useState([]);
     const toggleComplet = i =>
     setTodos(
          todos.map(
               (todo, k) =>{
                    k === i
                    ?{
                         ...todo,
                         complete:!todo.complete
                    }:todo
               }
          )
     )
     return (
          <div className='App'>
              <Form onSubmit={text => setTodos([{text, complete: false}, ...todos])}/>
              <div>
                   {
                        todos.map(({text}) => (
                        <div key={text}
                              onClick={() => toggleComplet(i)}
                              style={{textDecoration: complete ? "line-through":""}}>
                             {text}
                         </div>
                        ))
                   }
              </div>
              <button onClick={() => setTodos}></button>
          </div>
     )
}
