import React,  {useState} from "react";
import "./App.css"


export default () => {
     const [todos, setTodos] = useState([])
     const  handleChange = (event) => {
          setTodos({
               ...state,
               [event.target.value] : todos
          })
          console.log('setTodos: ', event.target.value)
     }
     return (
          <div>
               <form className='form-todolist'>
                    <input type='text' placeholder='todoList...' onChange={handleChange}>     
                    </input>
                    <button type='submit'>
                    Add
                    </button>
               </form>
          </div>
     )
}
