import React,  {useState} from "react";
import "./App.css"



export default () => {
     const [todos, setTodos] = useState([])
     const [value, setValue] = useState('')
     const  handleInputChange = (event) => {
          console.log('setTodos: ', event.target.value)
          
     return {
          value,
          onChange: e => setValue(e.target.value),
          resetValue: () => setValue("")
          };
     }

     return (
          <div>
               <form className='form-todolist'>
                    <input type='text' placeholder='todoList...' onChange={handleInputChange}>     
                    </input>
                    <button type='submit' onSubmit={e => e.preventDefault()}>
                    Add
                    </button>
               </form>
          </div>
     )
}
