import React, { useState } from "react";
import "./App.css"



export default () => {
	const [todos, setTodos] = useState([])
	const [value, setValue] = useState('')

	const handleInputChange = (event) => {
		setValue(event.target.value)
	}

	const handlebtnClick = (e) => {
		
		if(value !== null){
			const newTodos = [...todos, value]
			console.log('rest1', [...todos])
	
			console.log('newTodos', newTodos)
			// todos.push(newTodos)
			console.log('newtodo: ', newTodos)
			setTodos(newTodos)
			setValue('')
		}
		e.preventDefault()
	}
	const removeTodos = index => {
		const newTodo  = todos.filter(item => todos.indexOf(item) !== index)
		setTodos(newTodo)

		// const newTodos = [...todos]
		// newTodos.slice(index,1)
		// setTodos(newTodos)
		// console.log('newTodos sau khi xóa: ', newTodos)
	}
	// console.log('value: ', value)
	// console.log('todos: ', todos)
	return (
		<div>
			<form className='form-todolist'>
				<input value={value} type='text' placeholder='todoList...' onChange={handleInputChange}>
				</input>
				<button onClick={(e) => { handlebtnClick(e)}}>
					Add
        </button>
				<div>
				{todos.map(item => 
				<div className="item">
					<div key={item}>{item}</div>
					<button  onClick={() => removeTodos(todos.indexOf(item))} className='del'>delete</button>
				</div>	
				)}
			</div>
			</form>
		</div>
	)
}
