import React, {useState} from "react";
import "./App.css"
import Form from "./Form";

export default () => {
     const [todos, setTodos] = useState([]);
     const toggleComplete = (i) => setTodos(todos)

     return (
          <div className='App'>
              <Form onSubmit={text => setTodos([{text, complete: false}, ...todos])}/>
              <div>
                   {
                        todos.map(({text}) => (
                        <div key={text}>{text}</div>
                        ))
                   }
              </div>
          </div>
     )
}
